import { Component } from '@angular/core';
import { SharedService } from '../shared.service';
// API KEY - 4O7kxB74PxdUdOoK17f0pcqLZn4kJAmUDJrWYgbt

@Component({
  selector: 'app-six',
  templateUrl: './six.component.html',
  styleUrls: ['./six.component.css']
})
export class SixComponent {
  inputValue: string = '';
  constructor(private sharedService: SharedService) { }

  saveInput() {
    console.log(this.inputValue);
    if (this.inputValue.length != 40) {
      alert("Кол-во символов в API ключе должно быть 40.")
    }
    this.sharedService.key = this.inputValue;
    this.sharedService.acess = true;
  }
  openLink() {
    const url = 'https://api.nasa.gov';
    window.open(url, '_blank');
  }
}
<input
  class="api-field"
  [(ngModel)]="inputValue"
  placeholder="Введите свой API ключ"
/>
<h4 class="text-warn" (click)="openLink()" class="link-text">
  получить API ключ NASA
</h4>
<button (click)="saveInput()" class="button">Подключиться</button>
